    <div className={classes.root}>
      <CssBaseline/>
      <SharedContext.Provider value= {{
        mailbox, setMailbox,
        drawerOpen, setDrawerOpen,
        toggleDrawerOpen,
      }}
      >
        <MailboxDrawer/>
        <AppBar/>
        <Content/>
      </SharedContext.Provider>
    </div>
  );
}

export default App;
