import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Hidden from '@material-ui/core/Hidden';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import AccountCircle from '@material-ui/icons/AccountCircle';

import SharedContext from './SharedContext';

const axios = require('axios');

const useStyles = makeStyles((theme) => ({
  paper: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
}));
/**
 * Prints Mailbox
 */
function getmail() {
  axios.get('http://localhost:3010/v0/mail')
      .then(function(response) {
        // allmail = response;
        // return allmail;
        console.log(response['data'][0].mail);
        return response['data'][0].mail;
      })
      .catch(function(error) {
        console.log(error);
      })
      .then(function() {
        console.log('yes');
      });
}
const mailbox = getmail;
// console.log(allmail);
/**
 * @return {object} JSX
 */
function Content() {
  const {mailbox} =
    React.useContext(SharedContext);

  const classes = useStyles();
  return (
    <Paper className={classes.paper}>
      <Toolbar/>
      <Hidden smDown implementation="css">
        <h3>Desktop {mailbox}</h3>
      </Hidden>
      <Hidden mdUp implementation="css">
        <h3>Mobile {mailbox}</h3>
      </Hidden>
      <b>Whilst you can base you Assignment 9 submission on it,
        this example solution must not be re-distrubuted,
        doing so is considered Academic Misconduct.</b>
      <p/>
        To quote the official UCSC Academic Integrity policy
        as defined by the Office of the Registrar:
      <p/>
      <i>“Violations of the Academic Integrity policy can
        result in dismissal from the university and a permanent
        notation on a student’s transcript.”</i>
      <Button onClick={() => getmail()}>
        Get Mail
      </Button>
      <List>
        {mailbox.map((email) => (
          <ListItem button
            key={email.id}
          >
            <ListItemIcon>
              <AccountCircle />
            </ListItemIcon>
            <ListItemText primary={email.subject}/>
          </ListItem>
        ))}
      </List>
    </Paper>
  );
}

export default Content;
