import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
// import Hidden from '@material-ui/core/Hidden';
import Paper from '@material-ui/core/Paper';
// import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import AccountCircle from '@material-ui/icons/AccountCircle';
import Typography from '@material-ui/core/Typography';
import SharedContext from './SharedContext';

// const axios = require('axios');

const useStyles = makeStyles((theme) => ({
  paper: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
  mailbox: {
    width: '70vw',
  },
}));

/**
 * Prints & returns Mailbox
 * @param {function} setMail
 * @param {string} mailbox
 */
function getmail(setMail, mailbox) {
  fetch('http://localhost:3010/v0/mail?mailbox=' + mailbox)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setMail(json[0].mail);
      })
      .catch((error) => {
        console.log(error.toString());
      });
}

/**
 * @return {object} JSX
 */
function Content() {
  const {mailbox} =
    React.useContext(SharedContext);
  const [mail, setMail] = React.useState([]);
  // create local state called mail, setmail as mutator
  // pass setmail to getmail func
  // inside getmail call setmail at line 36
  const classes = useStyles();
  React.useEffect(() => {
    getmail(setMail, mailbox);
  }, [mailbox]);
  return (
    <Paper className={classes.paper}>
      <Toolbar/>
      {mailbox}
      <Typography className={classes.mailbox} noWrap='true'>
        <List>
          {mail.map((email) => (
            <ListItem button
              key={email.id}
            >
              <ListItemIcon>
                <AccountCircle />
              </ListItemIcon>
              <ListItemText primary={email.to.name} secondary={email.subject}/>
            </ListItem>
          ))}
        </List>
      </Typography>
    </Paper>
  );
}

export default Content;
