import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Hidden from '@material-ui/core/Hidden';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import AccountCircle from '@material-ui/icons/AccountCircle';

import SharedContext from './SharedContext';

// const axios = require('axios');

const useStyles = makeStyles((theme) => ({
  paper: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
}));

/**
 * Prints & returns Mailbox
 */
function getmail(setMail) {
  fetch('http://localhost:3010/v0/mail')
      .then((response) => {
        // console.log(response['data'][0].mail);
        // return response['data'][0].mail;
        return response.json();
      })
      .then((json) => {
        console.log(json[0].mail);
        setMail(json[0].mail);
      })
      .catch((error) => {
        console.log(error.toString());
      });
}

/**
 * @return {object} JSX
 */
function Content() {
  const {mailbox} =
    React.useContext(SharedContext);
  const [mail, setMail] = React.useState([]);
  // create local state called mail, setmail as mutator
  // pass setmail to getmail func
  // inside getmail call setmail at line 36
  const classes = useStyles();
  const test = getmail(setMail);
  return (
    <Paper className={classes.paper}>
      <Toolbar/>
      <Hidden smDown implementation="css">
        <h3>Desktop {mailbox}</h3>
      </Hidden>
      <Hidden mdUp implementation="css">
        <h3>Mobile {mailbox}</h3>
      </Hidden>
      <b>Whilst you can base you Assignment 9 submission on it,
        this example solution must not be re-distrubuted,
        doing so is considered Academic Misconduct.</b>
      <p/>
        To quote the official UCSC Academic Integrity policy
        as defined by the Office of the Registrar:
      <p/>
      <i>“Violations of the Academic Integrity policy can
        result in dismissal from the university and a permanent
        notation on a student’s transcript.”</i>
      <Button onClick={() => getmail()}>
        Get Mail
      </Button>
      <List>
        {mail.map((email) => (
          <ListItem button
            key={email.id}
          >
            <ListItemIcon>
              <AccountCircle />
            </ListItemIcon>
            <ListItemText primary={email.subject}/>
          </ListItem>
        ))}
      </List>
    </Paper>
  );
}

export default Content;
