import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import SaveIcon from '@material-ui/icons/Save';
import Toolbar from '@material-ui/core/Toolbar';
import SharedContext from './SharedContext';
import AppBar from '@material-ui/core/AppBar';
// code for the dialog taken from https://material-ui.com/components/dialogs/

const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'relative',
  },
  button: {
    edge: 'start',
    color: 'inherit',
  },
}));
/**
 * @return {object}
 */
export default function settingsView() {
  const classes = useStyles();
  const {openSettings, setOpenSettings} = React.useContext(SharedContext);

  const handleSettingsClose = () => {
    setOpenSettings(false);
  };

  return (
    <div>
      <Toolbar>
        <Dialog fullScreen open={openSettings} onClose={handleSettingsClose}>
          <Toolbar>
            <IconButton className={classes.button}
              onClick={handleSettingsClose} aria-label="close">
              <ArrowBackIcon />
            </IconButton>
            <IconButton edge='end' color='inherit'>
              <SaveIcon/>
            </IconButton>
          </Toolbar>
        </Dialog>
      </Toolbar>
    </div>
  );
}
